create table casas_acolhimento (id int8 generated by default as identity, cidade varchar(255), estado varchar(255), nome_casa varchar(255), site varchar(255), primary key (id));
create table parceiros (id int8 generated by default as identity, email varchar(255), mensagem varchar(255), nome_parceiro varchar(255), telefone varchar(255), primary key (id));
create table vagas_cursos (id int8 generated by default as identity, duracao varchar(255), estado_cidade varchar(255), nome_curso varchar(255), nome_instituicao varchar(255), turno varchar(255), primary key (id));
create table vagas_emprego (id int8 generated by default as identity, beneficios varchar(255), cargo varchar(255), estado_cidade varchar(255), nome_empresa varchar(255), salario varchar(255), primary key (id));
create table voluntarios (id int8 generated by default as identity, email varchar(255), idade int4, mensagem varchar(255), nome_voluntario varchar(255), telefone varchar(255), primary key (id));